sys_variable_value.value:
(function execute(inputs, outputs){
  try {
    var pLabel;
    var load_status =  true;  
    var l = new global.GSLog("sn_vul.usb.logging.verbosity", "BitSightImportReportProcessor");

    pLabel = "Instantiate BitSight_ReportProcessor Class";
    processor = new global.BitSightImportReportProcessor();

    pLabel = "Import Attachments and load ImportSet Table";
    processor.importAtt(inputs.datasource);

  } 
   catch(ex) {
          var e_msg = ex.message;
          load_status = false;
          l.logError(gs.getMessage('sn_vul', "Load Data: " + ex));
   }      
 }
)(inputs, outputs);

