sys_variable_value.value:
try {
    var xform_status = true;
    dsOBJ = inputs.datasource;
	var dcRef = new GlideRecord('sys_data_source');
    var gl = new global.GSLog("sn_vul.usb.logging.verbosity", "BitSightImport - XformAtt ");

	dcRef.addEncodedQuery('sys_id=' + dsOBJ.sys_id + '^RLQUERYsys_import_set.data_source,>=1^state=loaded^ENDRLQUERY');
	dcRef.query();

    while(dcRef.next()) {
		var processor = new global.BitSightImportReportProcessor();
		var mapSource = processor.getXformMapBySysID(dcRef.import_set_table_name);
		if (mapSource != "undefined") processor.XformAtt(dcRef, mapSource);
	    else throw new Error("***** No Transform Map was found. *****");
	}
}
catch(e) {
    xform_status = false;
	gl.logError(gs.getMessage('sn_vul',  "BitSightImport - XformAtt " +  e));
}

