sys_variable_value.value:
(function execute(inputs, outputs) {

  var deleteLog = "BitSight Remove Attachment Files from Data Source: " + inputs.dataSource + " <Delete Count -> ";
  var deleteImpSet = "BitSight ImportSets attached to Data Source: " + inputs.dataSource + " <Delete Count -> ";
  var deleteCount = 0;

  var attachment = new GlideSysAttachment();
  var agr = attachment.getAttachments('sys_data_source', inputs.dataSource.sys_id);

  while(agr.next()){
    attachment.deleteAttachment(agr.getValue('sys_id'));
    deleteCount = deleteCount+1;
  }

  gs.info(deleteLog + deleteCount);

})(inputs, outputs);

