sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
    var l = new global.GSLog("sn_vul.usb.logging.verbosity", "BitSightImportReportProcessor");
    var dataSourceId = import_set.data_source.sys_id;

    try {

        var attachmentApi = new GlideSysAttachment();
        var gr = new GlideRecord("sys_attachment");
        gr.addQuery("table_name", "sys_data_source");
        gr.addQuery("table_sys_id", dataSourceId);
        gr.orderByDesc('sys_updated_on');
        gr.setLimit(1);
        gr.query();
        gr.next();
        var attachmentId = gr.sys_id + "";

        processor = new global.BitSightImportReportProcessor();
        processor.processReport(attachmentId, "ssl_certs");
    } catch (ex) {
        l.logError(gs.getMessage('sn_vul', "BS-APPSEC INVOKE Error: " + ex));
    }
})(source, map, log, target);
